import React, { useState, useCallback, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import { PhotoIcon, XMarkIcon, ArrowPathIcon } from '@heroicons/react/24/outline';
import axios from 'axios';

function ReportForm({ selectedLocation }) {
  const [formData, setFormData] = useState({
    reportType: '',
    description: '',
    location: null,
    image: null,
    imagePreview: null,
    imageAnalysis: null
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);

  // Update location when selectedLocation changes
  useEffect(() => {
    if (selectedLocation) {
      setFormData(prev => ({
        ...prev,
        location: {
          latitude: selectedLocation.lat,
          longitude: selectedLocation.lng
        }
      }));
    }
  }, [selectedLocation]);

  const onDrop = useCallback(async (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (file) {
      // Reset states
      setUploadError(null);
      setIsUploading(true);

      // Preview image
      const reader = new FileReader();
      reader.onload = () => {
        setFormData(prev => ({
          ...prev,
          image: file,
          imagePreview: reader.result
        }));
      };
      reader.readAsDataURL(file);

      // Upload and analyze image
      const formData = new FormData();
      formData.append('image', file);

      try {
        const response = await axios.post('http://localhost:5000/api/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          },
          timeout: 30000 // 30 second timeout
        });

        if (!response.data || !response.data.analysis) {
          throw new Error('Invalid response from server');
        }

        const { analysis } = response.data;
        
        // Validate analysis data
        if (!analysis.suggestedReportType || !analysis.description) {
          throw new Error('Incomplete analysis data received');
        }

        setFormData(prev => ({
          ...prev,
          imageAnalysis: analysis,
          reportType: analysis.suggestedReportType || prev.reportType
        }));
      } catch (error) {
        console.error('Error uploading image:', error);
        let errorMessage = 'Failed to analyze image. Please try again.';
        
        if (error.response) {
          // Server responded with an error
          errorMessage = error.response.data.error || error.response.data.details || errorMessage;
        } else if (error.request) {
          // Request was made but no response received
          errorMessage = 'No response from server. Please check your connection.';
        } else if (error.code === 'ECONNABORTED') {
          errorMessage = 'Request timed out. Please try again.';
        }
        
        setUploadError(errorMessage);
        // Clear the image preview and analysis on error
        setFormData(prev => ({
          ...prev,
          image: null,
          imagePreview: null,
          imageAnalysis: null
        }));
      } finally {
        setIsUploading(false);
      }
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif']
    },
    maxSize: 5242880, // 5MB
    multiple: false
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.location) {
      alert('Please select a location on the map');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/reports', {
        reportType: formData.reportType,
        description: formData.description,
        location: formData.location,
        imageUrl: formData.imageAnalysis?.imageUrl,
        imageAnalysis: formData.imageAnalysis
      });

      console.log('Report submitted:', response.data);
      
      // Reset form
      setFormData({
        reportType: '',
        description: '',
        location: null,
        image: null,
        imagePreview: null,
        imageAnalysis: null
      });
    } catch (error) {
      console.error('Error submitting report:', error);
      if (error.response) {
        console.error('Error details:', error.response.data);
      }
    }
  };

  // Helper function to format the AI response
  const formatAIResponse = (text) => {
    if (!text) return '';
    
    // Split the text into paragraphs
    const paragraphs = text.split('\n').filter(p => p.trim());
    
    // Format each paragraph
    return paragraphs.map((paragraph, index) => (
      <p key={index} className="mb-2">
        {paragraph}
      </p>
    ));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <label className="block text-sm font-medium text-gray-700">
          Report Type
        </label>
        <select
          value={formData.reportType}
          onChange={(e) => setFormData(prev => ({ ...prev, reportType: e.target.value }))}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
          required
        >
          <option value="">Select a type</option>
          <option value="theft">Theft</option>
          <option value="vandalism">Vandalism</option>
          <option value="accident">Accident</option>
          <option value="suspicious">Suspicious Activity</option>
          <option value="other">Other</option>
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">
          Description
        </label>
        <textarea
          value={formData.description}
          onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
          rows={4}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
          required
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">
          Location
        </label>
        <div className="mt-1 text-sm text-gray-500">
          {formData.location ? (
            <p>Selected location: {formData.location.latitude.toFixed(6)}, {formData.location.longitude.toFixed(6)}</p>
          ) : (
            <p>Please select a location on the map</p>
          )}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">
          Upload Image
        </label>
        <div
          {...getRootProps()}
          className={`mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md ${
            isDragActive ? 'border-primary-500 bg-primary-50' : ''
          } ${isUploading ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}`}
        >
          <div className="space-y-1 text-center">
            {isUploading ? (
              <ArrowPathIcon className="mx-auto h-12 w-12 text-gray-400 animate-spin" />
            ) : (
              <PhotoIcon className="mx-auto h-12 w-12 text-gray-400" />
            )}
            <div className="flex text-sm text-gray-600">
              <input {...getInputProps()} disabled={isUploading} />
              <p className="pl-1">
                {isUploading ? 'Analyzing image...' : 'Drag and drop an image, or click to select'}
              </p>
            </div>
            <p className="text-xs text-gray-500">PNG, JPG, GIF up to 5MB</p>
          </div>
        </div>
      </div>

      {uploadError && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-md text-sm">
          {uploadError}
        </div>
      )}

      {formData.imagePreview && (
        <div className="relative">
          <img
            src={formData.imagePreview}
            alt="Preview"
            className="h-32 w-32 object-cover rounded-lg"
          />
          <button
            type="button"
            onClick={() => setFormData(prev => ({ ...prev, image: null, imagePreview: null, imageAnalysis: null }))}
            className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
          >
            <XMarkIcon className="h-4 w-4" />
          </button>
        </div>
      )}

      {formData.imageAnalysis && (
        <div className="bg-white border border-gray-200 rounded-lg shadow-sm overflow-hidden">
          <div className="px-4 py-5 sm:p-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Image Analysis Results</h3>
            
            <div className="space-y-4">
              {/* Report Type Section */}
              <div className="bg-blue-50 border-l-4 border-blue-400 p-4">
                <div className="flex">
                  <div className="flex-shrink-0">
                    <svg className="h-5 w-5 text-blue-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                    </svg>
                  </div>
                  <div className="ml-3">
                    <h4 className="text-sm font-medium text-blue-800">Suggested Report Type</h4>
                    <div className="mt-1">
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                        {formData.imageAnalysis.suggestedReportType}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Analysis Description Section */}
              <div className="bg-gray-50 rounded-md p-4">
                <h4 className="text-sm font-medium text-gray-700 mb-2">Detailed Analysis</h4>
                <div className="text-sm text-gray-600 space-y-2">
                  {formatAIResponse(formData.imageAnalysis.description)}
                </div>
              </div>

              {/* Confidence Score Section */}
              {formData.imageAnalysis.confidence && (
                <div className="bg-gray-50 rounded-md p-4">
                  <h4 className="text-sm font-medium text-gray-700 mb-2">Confidence Score</h4>
                  <div className="w-full bg-gray-200 rounded-full h-2.5">
                    <div 
                      className="bg-blue-600 h-2.5 rounded-full" 
                      style={{ width: `${formData.imageAnalysis.confidence * 100}%` }}
                    ></div>
                  </div>
                  <p className="text-xs text-gray-500 mt-1">
                    {Math.round(formData.imageAnalysis.confidence * 100)}% confidence in analysis
                  </p>
                </div>
              )}

              {/* Model Information */}
              {formData.imageAnalysis.modelUsed && (
                <div className="text-xs text-gray-500 mt-2">
                  Analysis performed using: {formData.imageAnalysis.modelUsed}
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      <button
        type="submit"
        className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
      >
        Submit Report
      </button>
    </form>
  );
}

export default ReportForm;
