import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { PhotoIcon, XMarkIcon } from '@heroicons/react/24/outline';
import axios from 'axios';

function ReportForm() {
  const [formData, setFormData] = useState({
    reportType: '',
    description: '',
    location: null,
    image: null,
    imagePreview: null,
    imageAnalysis: null
  });

  const onDrop = useCallback(async (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setFormData(prev => ({
          ...prev,
          image: file,
          imagePreview: reader.result
        }));
      };
      reader.readAsDataURL(file);

      // Upload and analyze image
      const formData = new FormData();
      formData.append('image', file);

      try {
        const response = await axios.post('http://localhost:5000/api/upload', formData);
        setFormData(prev => ({
          ...prev,
          imageAnalysis: response.data.analysis,
          reportType: response.data.analysis.suggestedReportType
        }));
      } catch (error) {
        console.error('Error uploading image:', error);
      }
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif']
    },
    maxSize: 5242880 // 5MB
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/reports', formData);
      console.log('Report submitted:', response.data);
      // Reset form
      setFormData({
        reportType: '',
        description: '',
        location: null,
        image: null,
        imagePreview: null,
        imageAnalysis: null
      });
    } catch (error) {
      console.error('Error submitting report:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <label className="block text-sm font-medium text-gray-700">
          Report Type
        </label>
        <select
          value={formData.reportType}
          onChange={(e) => setFormData(prev => ({ ...prev, reportType: e.target.value }))}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
          required
        >
          <option value="">Select a type</option>
          <option value="theft">Theft</option>
          <option value="vandalism">Vandalism</option>
          <option value="accident">Accident</option>
          <option value="suspicious">Suspicious Activity</option>
          <option value="other">Other</option>
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">
          Description
        </label>
        <textarea
          value={formData.description}
          onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
          rows={4}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
          required
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">
          Upload Image
        </label>
        <div
          {...getRootProps()}
          className={`mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md ${
            isDragActive ? 'border-primary-500 bg-primary-50' : ''
          }`}
        >
          <div className="space-y-1 text-center">
            <PhotoIcon className="mx-auto h-12 w-12 text-gray-400" />
            <div className="flex text-sm text-gray-600">
              <input {...getInputProps()} />
              <p className="pl-1">Drag and drop an image, or click to select</p>
            </div>
            <p className="text-xs text-gray-500">PNG, JPG, GIF up to 5MB</p>
          </div>
        </div>
      </div>

      {formData.imagePreview && (
        <div className="relative">
          <img
            src={formData.imagePreview}
            alt="Preview"
            className="h-32 w-32 object-cover rounded-lg"
          />
          <button
            type="button"
            onClick={() => setFormData(prev => ({ ...prev, image: null, imagePreview: null }))}
            className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1"
          >
            <XMarkIcon className="h-4 w-4" />
          </button>
        </div>
      )}

      {formData.imageAnalysis && (
        <div className="bg-gray-50 p-4 rounded-lg">
          <h3 className="text-sm font-medium text-gray-700">Image Analysis</h3>
          <p className="mt-1 text-sm text-gray-600">
            Suggested Report Type: {formData.imageAnalysis.suggestedReportType}
          </p>
          <div className="mt-2">
            <h4 className="text-xs font-medium text-gray-500">Detected Labels:</h4>
            <div className="mt-1 flex flex-wrap gap-2">
              {formData.imageAnalysis.labels.map((label, index) => (
                <span
                  key={index}
                  className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-primary-100 text-primary-800"
                >
                  {label.description}
                </span>
              ))}
            </div>
          </div>
        </div>
      )}

      <button
        type="submit"
        className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
      >
        Submit Report
      </button>
    </form>
  );
}

export default ReportForm;
